mapper.update:
update board
set board_count = board_count + 1
where board_id = #{bId}

update board
set board_title = #{boardTitle}, board_content = #{boardContent}, 
	original_filename = #{originalFileName}, rename_filename = #{renameFileName}
where board_id = #{boardId}

update board
set board_status = 'N'
where board_id = #{bId}
